import { useEffect, useState, useRef } from "react";
import "./Main.css";
import Typewriter from "typewriter-effect"
import { useNavigate } from "react-router-dom";

function Main() {
  const [options, setOptions] = useState([]);
  const [buckets, setBuckets] = useState([]);
  const [draggedItem, setDraggedItem] = useState(null);
  const [draggedItemSource, setDraggedItemSource] = useState(null);
  const [draggedItemSourceIndex, setDraggedItemSourceIndex] = useState(null);

  const navigate = useNavigate();

  const inputRef = useRef([]);
  const checkButtonRef = useRef(null);
  const optionsContainerRef = useRef(null);

  useEffect(() => {    
    const randomNumbers = Array.from({ length: 5 }, () =>
            Math.floor(Math.random() * 100)
    );
    setOptions(randomNumbers);
    setBuckets(Array(5).fill(null));

  },[]);

  const handleDragStart = (event, value, source, sourceIndex) => {
      setDraggedItem(value);
      setDraggedItemSource(source);
      setDraggedItemSourceIndex(sourceIndex);
  };

    const handleDragOver = (event) => {
        event.preventDefault();
    };

    const handleDropOption = (event, index) => {
        event.preventDefault();
        if (draggedItem !== null && draggedItemSource !== 'options') {
            if (draggedItemSource === 'buckets') {
                const updatedBuckets = [...buckets];
                updatedBuckets[draggedItemSourceIndex] = null;
                setBuckets(updatedBuckets);
            }

            if (options[index] === null || options[index] === 0) {
                const updatedOptions = [...options];
                updatedOptions[index] = draggedItem;
                setOptions(updatedOptions);
            } else {
                const updatedBuckets = [...buckets];
                updatedBuckets.push(options[index]);
                const updatedOptions = [...options];
                updatedOptions[index] = draggedItem;
                setBuckets(updatedBuckets);
                setOptions(updatedOptions);
            }

            setDraggedItem(null);
            setDraggedItemSource(null);
            setDraggedItemSourceIndex(null);
        }
    };

    const handleDropBucket = (event, index) => {
        event.preventDefault();
        if (draggedItem !== null && draggedItemSource !== 'buckets') {
            if (draggedItemSource === 'options') {
                const updatedOptions = [...options];
                updatedOptions[draggedItemSourceIndex] = null;
                setOptions(updatedOptions);
            }

            if (buckets[index] === null) {
                const updatedBuckets = [...buckets];
                updatedBuckets[index] = draggedItem;
                setBuckets(updatedBuckets);
            } else {
                const updatedOptions = [...options];
                updatedOptions.push(buckets[index]);
                const updatedBuckets = [...buckets];
                updatedBuckets[index] = draggedItem;
                setOptions(updatedOptions);
                setBuckets(updatedBuckets);
            }

            setDraggedItem(null);
            setDraggedItemSource(null);
            setDraggedItemSourceIndex(null);
        }
    };

  // function checkAnswer() {
  //   const values = inputValues.map((value) => parseInt(value) || 0);
  //   const sortedValues = values.slice().sort((a, b) => a - b);
  //   console.log(values, sortedValues);
  //   const resultMessage =
  //     JSON.stringify(values) === JSON.stringify(sortedValues)
  //       ? "Correct! You sorted the numbers correctly."
  //       : "Wrong! Please try again.";

  //   navigate(`/result?message=${resultMessage}`);
  // }

  return (
    <div className="main">
      <h1>
        <Typewriter 
          options={{
            autoStart: true,
            loop: true,
            delay: 80,
            strings: ["Arrange the numbers in ascending order"]
          }}
        />
      </h1>

      <div className="input-div">
        {buckets.map((bucket, index) => (
          <div
              key={index}
              className="star"
              draggable="true"
              onDragStart={(event) => handleDragStart(event, bucket, 'buckets', index)}
              onDragOver={handleDragOver}
              onDrop={(event) => handleDropBucket(event, index)}
          >
            {bucket !== null ? bucket : 'Drop'}
        </div>
    ))}
        {/* {inputValues.map((value, index) => (
         <div className="star">
          <input
            key={index}
            ref={(x) => (inputRef.current[index] = x)}
            value={value}
            type="text"
            placeholder="Drop"
            onDragOver={(ev) => ev.preventDefault()}
            onDrop={handleDrop}
            onChange={(ev) => handleChange(ev, index)}
            readOnly>
            </input>
            </div>          
        ))} */}
      </div>

      <div className="options-container" ref={optionsContainerRef}>
        {/* {options.map((option, index) => (
          <div
            key={index}
            className="option"
            draggable="true"
            onDragStart={handleDragStart}
            data-value={option}
          >
           <div>{option}</div> 
          </div>
        ))} */}
        {options.map((option, index) => (
          <div
              key={index}
              className="option"
              draggable="true"
              onDragStart={(event) => handleDragStart(event, option, 'options', index)}
              onDragOver={handleDragOver}
              onDrop={(event) => handleDropOption(event, index)}
          >
              {/* <span className="pr-2">&#8801;</span> */}
          {option}
          </div>
      ))}
      </div>

      {/* <button
        className={`check-button ${
          inputValues.filter((value) => value === "").length === 0 && "enabled"
        }`}
        onClick={checkAnswer}
        ref={checkButtonRef}
      >
        Check
      </button> */}
    </div>
  );
}

export default Main;
